changeSet.sql:
CREATE SEQUENCE currency_seq;
CREATE TABLE currency ( coin_name VARCHAR(50) NOT NULL , symbol VARCHAR(50) NOT NULL , market_cap VARCHAR(50) NOT NULL , price VARCHAR(50) NOT NULL , circ_supply VARCHAR(50) NOT NULL , block_chain VARCHAR(50) NOT NULL , rank INT(50) NOT NULL , logo_url VARCHAR(50) NOT NULL , date_added date NOT NULL , description text NOT NULL,
id INT NOT NULL DEFAULT NEXTVAL ('currency_seq') , PRIMARY KEY (id) ) ;
