changeSet.sql:
CREATE SEQUENCE currency_stats_seq;
CREATE TABLE currency_stats (
currency_id INT(50) NOT NULL, changes_24h VARCHAR(50) NOT NULL, changes_7d VARCHAR(50) NOT NULL, changes_30d VARCHAR(50) NOT NULL, changes_1Y VARCHAR(50) NOT NULL, all_time_high VARCHAR(50) NOT NULL, all_time_low VARCHAR(50) NOT NULL, foreign key(currency_id) references currency (id),
id INT NOT NULL DEFAULT NEXTVAL ('currency_stats_seq') , PRIMARY KEY (id) ) ;
